                unsafe
                {
                    byte[] data = new byte[sizeof(TCharDesc)];
                    fixed (byte* pb = data)
                    {
                        *(TCharDesc*)pb = CharDesc;
                    }
                    SendSocket(this.m_DefMsg, EncryptUnit.EncodeBuffer(data, sizeof(TCharDesc)));
