        /// <summary>
        /// 切换地图
        /// </summary>
        /// <param name="ProcessMsg"></param>
        /// <param name="boResult"></param>
        internal void ServerChangeMap(TProcessMessage ProcessMsg, ref bool boResult)
        {
            int nObjCount = -1;
            if (this.m_PEnvir.m_boDARK)
            {
                nObjCount = 1;
            }
            else
            {
                switch (m_nBright)
                {
                    case 1:
                        nObjCount = 0;
                        break;
                    case 3:
                        nObjCount = 1;
                        break;
                    default:
                        nObjCount = 1;
                        break;
                }
            }
            if (this.m_PEnvir.m_boDAY)
            {
                nObjCount = 0;
            }
            SendDefMessage(Grobal2.SM_CHANGEMAP, Parse(this), this.m_nCurrX, this.m_nCurrY, nObjCount, ProcessMsg.sMsg);
            RefUserState();
            SendMapDescription();
            SendServerConfig();// 发送服务端配置
            SendUnBind();
        }

