        internal unsafe void ClientAttack(TProcessMessage ProcessMsg, ref bool boResult)
        {
            // 开天斩重击// 开天斩轻击// 龙影剑法// 逐日剑法// 烈火// 4级烈火
            // 三绝杀// 追心刺// 断岳斩// 横扫千军// 倚天劈地// 血魄一击// 4级刺杀// 圆月弯刀
            int nCode = 124;
