        /// <summary>
        /// 客户端查询玩家名称
        /// </summary>
        /// <param name="Target"></param>
        /// <param name="x"></param>
        /// <param name="y"></param>
        private void ClientQueryUserName(TBaseObject Target, int x, int y)
        {
            string uname;
            int TagColor;
            if (this.CretInNearXY(Target, x, y))
            {
                TagColor = this.GetCharColor(Target);
                m_DefMsg = EncryptUnit.MakeDefaultMsg(Grobal2.SM_USERNAME, Parse(Target), TagColor, 0, 0, 0);
                uname = Target.GetShowName();
                SendSocket(this.m_DefMsg, EncryptUnit.EncodeString(uname));
            }
            else
            {
                SendDefMessage(Grobal2.SM_GHOST, Parse(Target), x, y, 0, "");
            }
        }

