                        if ((((tObjCount >= 3) && ((HUtil32.GetTickCount() - this.m_dwDupObjTick) > 3000)) || (((tObjCount == 2) 
                            && ((HUtil32.GetTickCount() - this.m_dwDupObjTick) > 10000))))
                            && ((HUtil32.GetTickCount() - this.m_dwDupObjTick) < 20000))
                        {
