                        {
                            this.SysMsg(GameMsgDef.g_sStoragePasswordLockedMsg, TMsgColor.c_Red, TMsgType.t_Hint);
                            m_boPasswordLocked = true;
                        }
                    }
                    return;
                }
                if (sData[0] != '@')
                {
                    if (sData.Length > GameConfig.nSayMsgMaxLen)
                    {
                        sData = sData.Substring(1 - 1, GameConfig.nSayMsgMaxLen);
                    }
