                //if ((sCmd).ToLower().CompareTo((M2Share.g_GameCommand.SysMsg.sCmd).ToLower()) == 0)
                //{
                //    CmdSysMsg(M2Share.g_GameCommand.SysMsg, SC); // 千里传音 
                //    return;
                //}
