                    return;
                }
                if (string.Compare(sCmd, M2Share.g_GameCommand.UNLOCKSTORAGE.sCmd, true) == 0)
                {
                    if (!GameConfig.boPasswordLockSystem)
                    {
                        this.SysMsg(GameMsgDef.g_sNoPasswordLockSystemMsg, TMsgColor.c_Red, TMsgType.t_Hint);
