        /// <summary>
        /// 玩家仍金币
        /// </summary>
        /// <param name="nGold"></param>
        /// <returns></returns>
        private bool ClientDropGold(int nGold)
        {
            bool result = false;
            if (GameConfig.boInSafeDisableDrop && this.InSafeZone())
            {
                this.SendMsg(M2Share.g_ManageNPC, Grobal2.RM_MENU_OK, 0, Parse(this), 0, 0, GameMsgDef.g_sCanotDropInSafeZoneMsg);
                return result;
            }
            if (GameConfig.boControlDropItem && (nGold < GameConfig.nCanDropGold))
            {
                this.SendMsg(M2Share.g_ManageNPC, Grobal2.RM_MENU_OK, 0, Parse(this), 0, 0, GameMsgDef.g_sCanotDropGoldMsg);
                return result;
            }
            if (!m_boCanDrop)
            {
                this.SendMsg(M2Share.g_ManageNPC, Grobal2.RM_MENU_OK, 0, Parse(this), 0, 0, GameMsgDef.g_sCanotDropItemMsg);
                return result;
            }
            if (nGold >= this.m_nGold)
            {
                return result;
            }
            this.m_nGold -= nGold;
            if (!this.DropGoldDown(nGold, false, null, this))
            {
                this.m_nGold += nGold;
            }
            this.GoldChanged();
            result = true;
            return result;
        }

