                                    nCode = 4;
                                    if (this.CheckItemValue(UserItem, 0)) // 检查物品是否禁止扔
                                    {
                                        break;
                                    }
                                    nCode = 5;
                                    if (this.PlugOfCheckCanItem(0, HUtil32.SBytePtrToString(StdItem->Name, StdItem->NameLen), false, 0, 0)) // 禁止物品规则(管理插件功能)
                                    {
                                        break;
