                                if (m_boHeroLogOut)// 英雄退出,则失败
                                {

                                    goto FailExit;
                                }
                                if (((THeroObject)(m_MyHero)).AddItemToBag(UserItem))
                                {
                                    this.m_ItemList.RemoveAt(I);
                                    ((THeroObject)(m_MyHero)).SendAddItem(UserItem);
                                    IsItem_51(1);// 发送聚灵珠的经验
