                                }
                                else
                                {
                                    n10 = -3;
                                }
                                break;
                            }
                        }
                    }
                    break;
                case Grobal2.CM_SENDITEMTOMASTERBAG:// 英雄包裹到主人包裹
                    for (I = m_MyHero.m_ItemList.Count - 1; I >= 0; I--)
                    {
                        if (m_MyHero.m_ItemList.Count <= 0)
                        {
                            break;
                        }
