                                // FrmDB.SaveSellOffItemList;//保存元宝寄售列表
                                TUserItem* UseItems = (TUserItem*)DealOffInfo->UseItemsBuff;
                                for (int K = 0; K <= 9; K++)
                                {
                                    StdItem = UserEngine.GetStdItem(UseItems[K].wIndex);
