                                    {
                                        nCode = 12;
                                        UserItem = (TUserItem*)Marshal.AllocHGlobal(sizeof(TUserItem));
                                        UserItem = UseItems;
                                        nCode = 13;
                                        if (IsEnoughBag()) // 检查人物的包裹是否满了
                                        {
                                            ClearCopyItem(0, UserItem->wIndex, UserItem->MakeIndex);// 清理包裹和仓库复制物品
