                                                        + HUtil32.HiWord(StdItem->SC) + ")" + "(" + HUtil32.LoWord(StdItem->AC) + "/"
                                                        + HUtil32.HiWord(StdItem->AC) + ")" + "(" + (HUtil32.LoWord(StdItem->MAC)).ToString() + "/"
                                                        + HUtil32.HiWord(StdItem->MAC) + ")" + UserItem->btValue[0] + "/" + (UserItem->btValue[1]).ToString()
                                                        + "/" + (UserItem->btValue[2]).ToString() + "/" + (UserItem->btValue[3]).ToString() + "/" + (UserItem->btValue[4]).ToString()
                                                        + "/" + UserItem->btValue[5] + "/" + (UserItem->btValue[6]).ToString() + "/" + (UserItem->btValue[7]).ToString()
                                                        + "/" + (UserItem->btValue[8]).ToString() + "/" + (UserItem->btValue[14]).ToString() + "\09"
                                                        + HUtil32.SBytePtrToString(DealOffInfo->sDealCharName, 0, DealOffInfo->DealCharNameLen));
                                                }
