        public void ClearViewRange()
        {
            try
            {
                this.m_VisibleHumanList.Clear();
                if (this.m_VisibleItems.Count > 0)
                {
                    for (int I = 0; I < this.m_VisibleItems.Count; I++)
                    {
                        if (this.m_VisibleItems[I] != null)
                        {
