        /// <summary>
        /// 人物下线或者死亡
        /// </summary>
        public override void MakeGhost()
        {
            string sSayMsg;
            TPlayObject Human;
            const string sExceptionMsg = "{异常} TPlayObject::MakeGhost";
            try
            {
                if ((M2Share.g_HighLevelHuman == this))
                {
                    M2Share.g_HighLevelHuman = null;
                }
                if ((M2Share.g_HighPKPointHuman == this))
