                }
            }
            if (!bo09)
            {
                SendDefMessage(Grobal2.SM_CHALLENCHGGOLD_FAIL, m_nChallengeGolds, this.m_nGold, (ushort)HUtil32.HiWord(this.m_nGold), 0, "");
