                m_ChallengeCreat.m_ChallengeTime = 0;
                m_ChallengeTime = 0;
                if (m_ChallengeCreat.m_ChallengeItemList.Count > 0)
                {
                    for (int I = 0; I < m_ChallengeCreat.m_ChallengeItemList.Count; I++)
                    {
                        UserItem = (TUserItem*)m_ChallengeCreat.m_ChallengeItemList[I];
