                if ((Math.Abs(PlayObject.m_nCurrX - nX) <= nRange) && (Math.Abs(PlayObject.m_nCurrY - nY) <= nRange))
                {
                    result = true;
                }
            }
            catch
            {
                M2Share.MainOutMessage("{异常} TNormNpc.ConditionOfCheckInMapRange");
