                    case '+':
                        nPoint = HUtil32._MAX(0, PlayObject.m_nPkPoint + nPKPOINT);
                        PlayObject.m_nPkPoint = nPoint;
                        break;
                }
                if (nOldPKLevel != PlayObject.PKLevel())
