            string sMap;
            try
            {
                // ----------------------------------------------------------------------------------------
                if (QuestActionInfo.sParam6 == "88")
                {
                    PlayObject = UserEngine.GetPlayObject(GetLineVariableText(PlayObject, QuestActionInfo.sParam7));
