                else
                {
                    Magic = UserEngine.FindMagic(sMagicName);
                    if (Magic == null)
                    {
                        ScriptActionError(PlayObject, "", QuestActionInfo, ScriptDef.sSC_DELSKILL);
                        return;
                    }
                    for (I = PlayObject.m_MagicList.Count - 1; I >= 0; I--)
                    {
                        if (PlayObject.m_MagicList.Count <= 0)
                        {
