                        {
                            PlayObject.m_MagicList.RemoveAt(I);
                            PlayObject.SendDelMagic(UserMagic);
                            Marshal.FreeHGlobal((IntPtr)UserMagic);
                            break;
                        }
                    }
                    PlayObject.RecalcAbilitys();
                    PlayObject.CompareSuitItem(false);//套装
                }
            }
            catch
            {
