                DynamicVar.VarType = VarType;
                DynamicVar.nInternet = nVarValue;
                DynamicVar.sString = sVarValue;
                boFoundVar = false;
                DynamicVarList = GetDynamicVarList(PlayObject, sType, ref sName);
