                {
                    Dispose(DynamicVar);
                    ScriptActionError(PlayObject, string.Format(sVarTypeError, sType), QuestActionInfo, ScriptDef.sSC_VAR);
                    return;
                }
                if (DynamicVarList.Count > 0)
                {
                    for (int I = 0; I < DynamicVarList.Count; I++)
                    {
                        if (DynamicVarList[I].sName.ToLower().CompareTo((sVarName).ToLower()) == 0)
                        {
                            boFoundVar = true;
                            break;
                        }
                    }
                }
                if (!boFoundVar)
                {
                    DynamicVarList.Add(DynamicVar);
                }
                else
                {
                    Dispose(DynamicVar);
                    ScriptActionError(PlayObject, string.Format(sVarFound, sVarName, sType), QuestActionInfo, ScriptDef.sSC_VAR);
