                }
                sType = QuestActionInfo.sParam1;
                sVarName = QuestActionInfo.sParam2;
                sFileName = QuestActionInfo.sParam3;
                if (sFileName[0] == '\\')
                {
                    sFileName = sFileName.Substring(2 - 1, sFileName.Length - 1);
                }
                if (sFileName[1] == '\\')
                {
                    sFileName = sFileName.Substring(3 - 1, sFileName.Length - 2);
                }
